import React , {Component} from 'react';
import {API_URL,API_KEY,IMAGE_BASE_URL,BACKDROP_SIZE,POSTER_SIZE} from '../../config';
import './Home.css';
import HeroImage from '../elements/HeroImage/HeroImage';
import SearchBar from '../elements/SearchBar/SearchBar';
import FourColGird from '../elements/FourColGrid/FourColGrid';
import MovieThumb from '../elements/MovieThumb/MovieThumb';
import LoadMoreBtn from '../elements/LoadMoreBtn/LoadMoreBtn';
import Spinner from '../elements/Spinner/Spinner';



class Home extends Component{
    state = {
        movies:[],
        heroImage:null,
        loading:false,
        currentPage:0,
        totalPages:0,
        searchTerm:'',


    }
    componentDidMount(){
        this.setState({loading:true});
        const endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page=1`;
        this.fetchItems(endpoint);
        
    }
    searchItems = (searchTerm) =>{
        console.log(searchTerm);
        let endpoint = '';
        this.setState({
            movies:[],
            loading:true,
            searchTerm

        });
        if(searchTerm === ''){
            endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page=1`;
        
        }else{
             endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&query=${searchTerm}`;
          

        }
    
        this.fetchItems(endpoint);
    }
    fetchItems = (endpoint)=>{
        
        fetch(endpoint)
        .then(result =>result.json())
        .then(result=>{
            this.setState({
                movies:[...this.state.movies,...result.results],
                heroImage:this.state.heroImage || result.results[0],
                loading:false,
                currentPage:result.page,
                totalPages:result.total_page,

            });
            console.log(result);
        });

    }
    loadMoreItems = ()=>{
        let endpoint = '';
        this.setState({loading:true});
        if(this.state.searchTerm === ''){
            const endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page=${this.state.currentPage + 1}`;
        }else{
            const endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&query=${this.state.searchTerm}&page=${this.state.currentPage + 1}`;
        }
        this.fetchItems(endpoint);

    }

    render(){
        return (
            <div className='rmdb-home'> 
            {this.state.heroImage ? 
            <div>
                <HeroImage  image={`${IMAGE_BASE_URL}${BACKDROP_SIZE}/${this.state.heroImage.backdrop_path }`}
                        title={this.state.heroImage.original_title}
                        text={this.state.heroImage.overview}
                />
                <SearchBar  callback={this.searchItems}/>
            </div> : null}
              <div className='rmdb-home-grid'>
              <FourColGird header={this.state.searchTerm?'Search Result':'Popular Moveies'}
              loading={this.state.loading}
              >
                  {this.state.movies.map ((element,i)=>{
                      return <MovieThumb 
                        key={i}
                        clickable={true}
                        image={element.poster_path ? `${IMAGE_BASE_URL}${POSTER_SIZE}${element.poster_path}`:'./images/no_image.jpg'}
                        movieId={element.id}
                        movieName={element.original_title}
                      />
                  })}
                  </FourColGird>
              </div>
                <Spinner />
                <LoadMoreBtn />
            </div>
        )
    }
}
export default Home;
